import React, { useState } from "react";
import "./App.css";
import HorizontalNav from "./components/Horizontalnav";
import VerticalNav from "./components/VerticalNav";
import Chat from "./components/chat";
import AuthButtons from "./components/AuthButtons";
import Group from "./components/group";
import BlogList from "./components/Bloglist";
import BlogEditor from "./components/Blogeditor";
import Home from './components/Home';
import Materials from "./components/Materials";

function App() {
    const [activeSection, setActiveSection] = useState("Home"); // Tracks current section
    const [blogs, setBlogs] = useState([]); // Stores blogs
    const [isWriting, setIsWriting] = useState(false); // Tracks blog editor state

    // Handles navigation click
    const handleNavClick = (section) => {
        setActiveSection(section);
        setIsWriting(false); // Close editor when navigating away
    };

    // Adds a new blog
    const addBlog = (newBlog) => {
        setBlogs([newBlog, ...blogs]);
        setIsWriting(false); // Close editor after saving
    };

    return (
        <div className="App">
            {/* Navigation Components */}
            <HorizontalNav onNavClick={handleNavClick} />
            <VerticalNav onNavClick={handleNavClick} />

            {/* Main Content Area */}
            <div className="content">
                {activeSection === "Home" && <Home />}
                {activeSection === "Login" && <AuthButtons />}
                {activeSection === "Chat" && <Chat />}
                {activeSection === "Groups" && <Group />}
                {activeSection === "Materials" && <Materials />}

                {/* Blog Section */}
                {activeSection === "Blog" && (
                    <div className={`page-section ${activeSection === "Blog" ? "active" : ""}`}>
                        {!isWriting && (
                            <button 
                                className="add-blog-btn"
                                onClick={() => setIsWriting(true)}>
                                + Add Blog
                            </button>
                        )}
                        {isWriting ? (
                            <BlogEditor onSave={addBlog} onCancel={() => setIsWriting(false)} />
                        ) : (
                            <BlogList blogs={blogs} />
                        )}
                    </div>
                )}
            </div>
        </div>
    );
}

export default App;
