import React, { useState } from "react";
import "./App.css";
import LoginAndRegister from "./components/LoginAndRegister";
import HorizontalNav from "./components/Horizontalnav";
import VerticalNav from "./components/VerticalNav";
import Chat from "./components/chat";  // Ensure the Chat component is properly named
import { getAuth, signInWithPopup, GoogleAuthProvider, signOut } from 'firebase/auth';
import { useAuthState } from 'react-firebase-hooks/auth';

// Initialize Firebase Authentication
const auth = getAuth();

function App() {
    const [activeSection, setActiveSection] = useState("Home");
    const [user] = useAuthState(auth);  // Firebase authentication state

    const handleNavClick = (section) => {
        setActiveSection(section);
    };

    return (
        <div className="App">
            <HorizontalNav />
            <VerticalNav onNavClick={handleNavClick} />

            {/* Render SignIn/SignOut buttons anywhere across the app */}
            <div className="auth-buttons">
                <SignInOutButtons user={user} />
            </div>

            <div className="content">
                {activeSection === "Login" && <LoginAndRegister />}
                {activeSection === "Chat" && <Chat />}
            </div>
        </div>
    );
}

// SignInOutButtons component for handling sign in and sign out
function SignInOutButtons({ user }) {
    return user ? <SignOut /> : <SignIn />;
}

// SignIn Component
function SignIn() {
    const signInWithGoogle = () => {
        const provider = new GoogleAuthProvider();
        signInWithPopup(auth, provider);
    };

    return (
        <button className="sign-in" onClick={signInWithGoogle}>Sign in with Google</button>
    );
}

// SignOut Component
function SignOut() {
    return (
        <button className="sign-out" onClick={() => signOut(auth)}>Sign Out</button>
    );
}

export default App;
