import React, { useRef, useState, useEffect } from 'react';
import './chat.css';
import { collection, addDoc, orderBy, query, where, getDocs, serverTimestamp, doc, setDoc } from 'firebase/firestore';
import { useAuthState } from 'react-firebase-hooks/auth';
import { useCollectionData } from 'react-firebase-hooks/firestore';
import { auth, firestore } from './firebase';
import AuthButtons from "./AuthButtons";

function Chat() {
    const [user] = useAuthState(auth);
    const [selectedFriend, setSelectedFriend] = useState(null); // For the selected friend's private chat

    return (
        <div className="App">
            <section>
                {user ? (
                    selectedFriend ? (
                        <PrivateChat friend={selectedFriend} />
                    ) : (
                        <FriendsList setSelectedFriend={setSelectedFriend} />
                    )
                ) : (
                    <AuthButtons />
                )}
            </section>
        </div>
    );
}

// Component to display the user's friends
function FriendsList({ setSelectedFriend }) {
    const [friends, setFriends] = useState([]);

    useEffect(() => {
        const fetchFriends = async () => {
            const currentUser = auth.currentUser;
            if (currentUser) {
                const userDocRef = doc(firestore, 'users', currentUser.uid);
                const userSnapshot = await getDocs(userDocRef);
                const userData = userSnapshot.data();

                if (userData && userData.friends) {
                    setFriends(userData.friends);
                }
            }
        };

        fetchFriends();
    }, []);

    return (
        <div className="friends-list">
            <h3>Your Friends</h3>
            {friends.length > 0 ? (
                friends.map((friendUid) => (
                    <FriendItem key={friendUid} friendUid={friendUid} setSelectedFriend={setSelectedFriend} />
                ))
            ) : (
                <p>No friends yet. Add some!</p>
            )}
        </div>
    );
}

// Component to display each friend's name
function FriendItem({ friendUid, setSelectedFriend }) {
    const [friendData, setFriendData] = useState(null);

    useEffect(() => {
        const fetchFriendData = async () => {
            const friendDocRef = doc(firestore, 'users', friendUid);
            const friendSnapshot = await getDocs(friendDocRef);
            setFriendData(friendSnapshot.data());
        };

        fetchFriendData();
    }, [friendUid]);

    return (
        <div className="friend-item" onClick={() => setSelectedFriend(friendData)}>
            <p>{friendData?.username || 'Loading...'}</p>
        </div>
    );
}

// Private chat component between the current user and a friend
function PrivateChat({ friend }) {
    const currentUser = auth.currentUser;
    const dummy = useRef();
    const [formValue, setFormValue] = useState('');
    const [messages, setMessages] = useState([]);

    const chatId = `${currentUser.uid}_${friend.uid}`; // Unique chat ID between two users

    useEffect(() => {
        const fetchMessages = async () => {
            const messagesRef = collection(firestore, 'chats', chatId, 'messages'); // Reference to the messages collection
            const q = query(messagesRef, orderBy('timestamp')); // Correctly create a query from the collection

            const querySnapshot = await getDocs(q); // Fetch the messages using the query
            const messagesData = [];
            querySnapshot.forEach((doc) => {
                messagesData.push(doc.data());
            });

            setMessages(messagesData);
        };

        fetchMessages();
    }, [chatId]);

    const sendMessage = async (e) => {
        e.preventDefault();

        const { uid, photoURL } = currentUser;

        // Add new message to the chat
        await addDoc(collection(firestore, 'chats', chatId, 'messages'), {
            text: formValue,
            timestamp: serverTimestamp(),
            sender: uid,
            photoURL,
        });

        setFormValue('');
        dummy.current.scrollIntoView({ behavior: 'smooth' });
    };

    return (
        <>
            <main>
                <h3>Chatting with {friend.username}</h3>
                {messages && messages.map((msg, idx) => <ChatMessage key={idx} message={msg} />)}
                <span ref={dummy}></span>
            </main>

            <form onSubmit={sendMessage}>
                <input value={formValue} onChange={(e) => setFormValue(e.target.value)} placeholder="Type a message" />
                <button type="submit" disabled={!formValue}>Send</button>
            </form>
        </>
    );
}


// Component to render each chat message
function ChatMessage({ message }) {
    const { text, sender, photoURL } = message;
    const messageClass = sender === auth.currentUser.uid ? 'sent' : 'received';

    return (
        <div className={`message ${messageClass}`}>
            <img src={photoURL || 'https://api.adorable.io/avatars/23/abott@adorable.png'} alt="Avatar" />
            <p>{text}</p>
        </div>
    );
}

export default Chat;
