import React, { useState } from 'react';
import { collection, query, where, getDocs, addDoc, serverTimestamp } from 'firebase/firestore'; // Firebase v9 functions

function GroupCreation({ friends, createGroup, closeModal }) {
    const [groupName, setGroupName] = useState('');
    const [selectedFriends, setSelectedFriends] = useState([]);

    const handleFriendSelect = (friendUid) => {
        if (selectedFriends.includes(friendUid)) {
            setSelectedFriends(selectedFriends.filter(uid => uid !== friendUid));
        } else {
            setSelectedFriends([...selectedFriends, friendUid]);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (groupName && selectedFriends.length > 0) {
            createGroup(groupName, selectedFriends);
            closeModal();  // Close modal after group is created
        }
    };

    return (
        <div className="group-creation-modal">
            <h2>Create a New Group</h2>
            <input
                type="text"
                placeholder="Group Name"
                value={groupName}
                onChange={(e) => setGroupName(e.target.value)}
            />
            <div className="friend-selection">
                {friends.map((friend) => (
                    <div key={friend.uid}>
                        <label>
                            <input
                                type="checkbox"
                                checked={selectedFriends.includes(friend.uid)}
                                onChange={() => handleFriendSelect(friend.uid)}
                            />
                            {friend.username}
                        </label>
                    </div>
                ))}
            </div>
            <button onClick={handleSubmit}>Create Group</button>
        </div>
    );
}

export default GroupCreation;
