// Chat.jsx

import React, { useState } from 'react';
import './chat.css';
import { useAuthState } from 'react-firebase-hooks/auth';
import { auth } from './firebase';
import AuthButtons from "./AuthButtons";
import FriendsList from "./FriendsList";  // Assuming you have this component created
import PrivateChat from "./PrivateChat";  // Import the PrivateChat component

function Chat() {
    const [user] = useAuthState(auth);
    const [selectedFriend, setSelectedFriend] = useState(null); // Selected friend's private chat

    return (
        <div className="App">
            <section>
                {user ? (
                    selectedFriend ? (
                        <PrivateChat friend={selectedFriend} />  // Use PrivateChat here
                    ) : (
                        <FriendsList setSelectedFriend={setSelectedFriend} />  // Friend list selection
                    )
                ) : (
                    <AuthButtons />
                )}
            </section>
        </div>
    );
}

export default Chat;
