import React, { useState } from "react";
import "./App.css";

import HorizontalNav from "./components/Horizontalnav";
import VerticalNav from "./components/VerticalNav";
import Chat from "./components/chat";
import AuthButtons from "./components/AuthButtons";
import Group from "./components/group";
import Materials from "./components/Materials";

function App() {
    const [activeSection, setActiveSection] = useState("Home");
    const [isVerticalNavActive, setIsVerticalNavActive] = useState(false); // State to control vertical nav visibility

    const handleNavClick = (section) => {
        setActiveSection(section);
    };

    const toggleVerticalNav = () => {
        setIsVerticalNavActive((prev) => !prev);
    };

    return (
        <div className="App">
            <HorizontalNav toggleVerticalNav={toggleVerticalNav} />
            <VerticalNav onNavClick={handleNavClick} isActive={isVerticalNavActive} />

            <div className="content">
                {activeSection === "Login" && <AuthButtons />}
                {activeSection === "Chat" && <Chat />}
                {activeSection === "Groups" && <Group />}
                {activeSection === "Materials" && <Materials />}
            </div>
        </div>
    );
}

export default App;
