import React, { useEffect, useState } from 'react';
import { doc, getDoc } from 'firebase/firestore';
import { auth, firestore } from './firebase'; // Import your firebase setup
function FriendsList({ friends, selectedFriends, setSelectedFriends }) {
    const toggleFriendSelection = (friendId) => {
        if (selectedFriends.includes(friendId)) {
            // Remove friend from selection
            setSelectedFriends(selectedFriends.filter(id => id !== friendId));
        } else {
            // Add friend to selection
            setSelectedFriends([...selectedFriends, friendId]);
        }
    };

    return (
        <div className="friends-list">
            {friends.map(friend => (
                <div
                    key={friend.id}
                    className={`friend-item ${selectedFriends.includes(friend.id) ? 'selected' : ''}`}
                    onClick={() => toggleFriendSelection(friend.id)}
                >
                    <img src={friend.photoURL || 'https://via.placeholder.com/30'} alt="Friend Avatar" />
                    <p>{friend.name}</p>
                </div>
            ))}
        </div>
    );
}

export default FriendsList;
